	[7723] = {"Curse These Fat Fingers",0,45,49,{59,250},51,0,0,0,0,0,0,0,0,"Hansel Heavyhands wants you to kill 20 Heavy War Golems.The Heavy War Golems wander the areas surrounding the Cauldron in the Searing Gorge. When you've killed enough, return to Hansel in Searing Gorge."},
	[7724] = {"Fiery Menace!",0,45,49,{59,250},51,0,0,0,0,0,0,0,0,"Hansel Heavyhands wants you to kill 20 Greater Lava Spiders in the Searing Gorge. You can find Greater Lava Spiders in the plateaus and lava pools of the Searing Gorge.Return to Hansel Heavyhands in the Searing Gorge when you have killed enough Greater Lava Spiders."},
	[7725] = {"Again With the Zapped Giants",0,45,55,{169,25},357,7003,0,0,0,0,1,0,0,"Use Zorbin's Ultra-Shrinker to zap any kind of giant found in Feralas into a more manageable form.  Bring 10 Miniaturization Residues found on the zapped versions of these giants to Zorbin Fandazzle at the docks of the Forgotten Coast, Feralas."},
	[7726] = {"Refuel for the Zapping",0,45,55,{169,25},357,7721,0,0,0,0,1,0,0,"Collect 6 Water Elemental Cores from the Sea Elementals and Sea Sprays along the Forgotten Coast of Feralas, and bring them back to Zorbin Fandazzle who is near the dock there."},
	[7727] = {"Incendosaurs? Whateverosaur is More Like It",0,45,49,{59,250},51,0,0,0,0,0,0,0,0,"Hansel Heavyhands wants you to kill 20 Incendosaurs.You will find Incendosaurs occupying the lava pools inside the Slag Pit. The Slag Pit can be found in the Cauldron of the Searing Gorge.Return to Hansel Heavyhands in the Searing Gorge when you have killed enough Incendosaurs."},
	[7728] = {"STOLEN: Smithing Tuyere and Lookout's Spyglass",0,45,48,{59,250},51,0,0,0,0,0,0,0,0,"Find and return the Smithing Tuyere and Lookout's Spyglass to Taskmaster Scrange in the Searing Gorge.The only information you have about these items is the following: They were definitely stolen by Dark Iron dwarves. The Smithing Tuyere is a blacksmithing tool used by blacksmiths and the Lookout's Spyglass is an invaluable monitoring tool to lookouts. "},
	[7729] = {"JOB OPPORTUNITY: Culling the Competition",0,45,48,{59,250},51,0,0,0,0,0,0,0,0,"Slay 15 Dark Iron Taskmasters and 15 Dark Iron Slavers. Return to Taskmaster Scrange in the Searing Gorge once you have accomplished this task."},
