	[7839] = {"Vilebranch Hooligans",2,44,48,{0,0},47,0,0,0,0,0,0,0,0,"Smith Slagtree at Revantusk Village in the Hinterlands wants you to find Slagtree's Lost Tools. Return to him once this task is complete.You recall Smith Slagtree mentioning that the tools might be at one of the Vilebranch temples in the northeastern region of the Hinterlands. You should also check Skulk Rock."},
	[7840] = {"Lard Lost His Lunch",2,44,49,{0,0},47,0,0,0,0,0,0,0,0,"Lard at Revantusk Village in the Hinterlands wants you to find Lard's Lunch. Return to him when this task is complete.Lard mentioned that he left it on the island to the north. Watch out for Vilebranch trolls."},
	[7841] = {"Message to the Wildhammer",2,44,48,{0,0},47,0,0,0,0,0,0,0,41,"Otho Moji'ko at Revantusk Village in the Hinterlands wants you to slaughter 8 Highvale Scouts, 5 Highvale Outrunners, 5 Highvale Rangers and 2 Highvale Marksman. Return to him when this task is complete.You can find the Highvale high elves at the Quel'Danil Lodge in the northwestern region of the Hinterlands."},
	[7842] = {"Another Message to the Wildhammer",2,44,48,{0,0},47,7841,0,0,0,0,0,0,41,"Otho Moji'ko at Revantusk Village in the Hinterlands wants you to bring him 1 Long Elegant Feather from the gryphons that inhabit the Hinterlands. Return to him once this task is complete.Gryphons are known to inhabit every region of the Hinterlands."},
	[7843] = {"The Final Message to the Wildhammer",2,44,50,{0,0},47,7842,0,0,0,0,2,0,41,"Otho Moji'ko at Revantusk Village in the Hinterlands wants you to travel to Aerie Peak in the western region of the Hinterlands and place the Final Message to the Wildhammer by the well in the center of town. Return to him once this task is complete.Beware of angry Wildhammer dwarves and unruly members of the Alliance."},
	[7844] = {"Cannibalistic Cousins",2,44,48,{0,0},47,0,0,0,0,0,0,0,0,"Mystic Yayo'jin at Revantusk Village in the Hinterlands wants you to kill 6 Vilebranch Scalpers and 2 Vilebranch Soothsayers. Return to her when this task is complete.Yayo'jin indicated that these trolls could be found near the Shaol'watha and Agol'watha temples in the north by northeastern region of the Hinterlands."},
	[7845] = {"Kidnapped Elder Torntusk!",2,46,51,{0,0},47,0,0,0,0,0,0,0,0,"Primal Torntusk at Revantusk Village in the Hinterlands wants you to rescue her mate, Elder Torntusk, from Jintha'alor.You have been told that he is being held prisoner atop Jintha'alor, the Vilebranch capital city located in the southern region of the Hinterlands."},
	[7846] = {"Recover the Key!",2,46,51,{0,0},47,7845,0,0,0,0,0,0,0,"Elder Torntusk at Jintha'alor wants you to kill Hitah'ya the Keeper to get the Shackle Key to his bindings. Return to Elder Torntusk when you have recovered the key."},
	[7847] = {"Return to Primal Torntusk",2,46,51,{0,0},47,7846,0,0,0,0,0,0,0,"Elder Torntusk at Jintha'alor wants you to return to Primal Torntusk at Revantusk Village with news of his rescue."},
	[7848] = {"Attunement to the Core",0,55,60,{0,0},2717,0,0,0,0,0,0,0,81,"Venture to the Molten Core entry portal in Blackrock Depths and recover a Core Fragment. Return to Lothos Riftwaker in Blackrock Mountain when you have recovered the Core Fragment."},
	[7849] = {"Separation Anxiety",2,46,50,{0,0},47,0,0,0,0,0,0,0,0,"Huntsman Markhor at Revantusk Village in the Hinterlands wants you to recover his twin brother's remains. Find and return Huntsman Malkhor's Skull and Huntsman Malkhor's Bones to Huntsman Markhor.Markhor mentioned that his brother's remains were fed to the oozes and wolves of the Vilebranch. He also mentioned that there is a refuse pit filled with oozes in Jintha'alor as well as a wolf den. Both of these places are near the top of the temple."},
	[7850] = {"Dark Vessels",2,46,50,{0,0},47,0,0,0,0,0,0,0,0,"Primal Torntusk at Revantusk Village in the Hinterlands wants you to recover 5 Vessels of Tainted Blood from Jintha'alor. Return to Primal Torntusk when this task is complete."},
	[7861] = {"Wanted: Vile Priestess Hexx and Her Minions",2,44,51,{0,0},47,0,0,0,0,0,0,0,0,"You have been ordered to slay Vile Priestess Hexx and 5 Vilebranch Aman'zasi Guards. See Primal Torntusk at Revantusk Village in the Hinterlands once this task is complete.Vile Priestess Hexx and the Aman'zasi Guards can be found atop Jintha'alor in the Hinterlands."},
	[7862] = {"Job Opening: Guard Captain of Revantusk Village",2,44,51,{0,0},47,0,0,0,0,0,0,0,0,"You have been tasked with the decimation of 5 Vilebranch Berserkers, 3 Vilebranch Shadow Hunters, 3 Vilebranch Blood Drinkers, and 2 Vilebranch Soul Eaters.Should you complete this task, return to Primal Torntusk at Revantusk Village in the Hinterlands."},
